const chai = require('chai');
const chaiHttp = require('chai-http');
const mongoose = require('mongoose');
const app = require('../app');
const Product = require('../models/product');

const { expect } = chai;

chai.use(chaiHttp);

describe('Product Endpoints', () => {
    before(async () => {
        await mongoose.connect('mongodb://localhost:27017/testdb', {
            useNewUrlParser: true,
            useUnifiedTopology: true,
        });
    });

    after(async () => {
        await mongoose.connection.close();
    });

    beforeEach(async () => {
        await Product.deleteMany({});
    });

    it('should create a new product', async () => {
        const newProduct = {
            name: 'New Product',
            description: 'This is a new product',
            price: 19.99,
            variants: [
                { name: 'New Variant 1', sku: 'NEW1', additionalCost: 3.0, stockCount: 20 },
            ],
        };

        const res = await chai.request(app).post('/api/products').send(newProduct);

        expect(res.status).to.equal(201);
        expect(res.body).to.have.property('message', 'Product created successfully');
        expect(res.body.product).to.have.property('_id');
        expect(res.body.product.name).to.equal(newProduct.name);
    });

    it('should retrieve all products', async () => {
        await Product.create({
            name: 'Product 1',
            description: 'Description 1',
            price: 15.99,
            variants: [{ name: 'Variant 1', sku: 'SKU1', additionalCost: 2.0, stockCount: 30 }],
        });

        await Product.create({
            name: 'Product 2',
            description: 'Description 2',
            price: 24.99,
            variants: [{ name: 'Variant 2', sku: 'SKU2', additionalCost: 1.5, stockCount: 25 }],
        });

        const res = await chai.request(app).get('/api/products');

        expect(res.status).to.equal(200);
        expect(res.body).to.be.an('array');
        expect(res.body).to.have.lengthOf(2);
        expect(res.body[0].name).to.equal('Product 1');
        expect(res.body[1].price).to.equal(24.99);
    });

    it('should retrieve a specific product by ID', async () => {
        const product = await Product.create({
            name: 'Product 1',
            description: 'Description 1',
            price: 15.99,
            variants: [{ name: 'Variant 1', sku: 'SKU1', additionalCost: 2.0, stockCount: 30 }],
        });

        const res = await chai.request(app).get(`/api/products/${product._id}`);

        expect(res.status).to.equal(200);
        expect(res.body).to.have.property('_id', product._id.toString());
        expect(res.body.name).to.equal('Product 1');
    });

    it('should update a product by ID', async () => {
        const product = await Product.create({
            name: 'Product 1',
            description: 'Description 1',
            price: 15.99,
            variants: [{ name: 'Variant 1', sku: 'SKU1', additionalCost: 2.0, stockCount: 30 }],
        });

        const updatedProduct = {
            name: 'Updated Product',
            description: 'Updated Description',
            price: 29.99,
            variants: [{ name: 'Updated Variant', sku: 'UPD1', additionalCost: 5.0, stockCount: 40 }],
        };

        const res = await chai.request(app).put(`/api/products/${product._id}`).send(updatedProduct);

        expect(res.status).to.equal(200);
        expect(res.body).to.have.property('message', 'Product updated successfully');
        expect(res.body.product).to.have.property('_id', product._id.toString());
        expect(res.body.product.name).to.equal('Updated Product');
    });

    it('should delete a product by ID', async () => {
        const product = await Product.create({
            name: 'Product 1',
            description: 'Description 1',
            price: 15.99,
            variants: [{ name: 'Variant 1', sku: 'SKU1', additionalCost: 2.0, stockCount: 30 }],
        });

        const res = await chai.request(app).delete(`/api/products/${product._id}`);

        expect(res.status).to.equal(200);
        expect(res.body).to.have.property('message', 'Product deleted successfully');

        // Verify that the product is no longer in the database
        const deletedProduct = await Product.findById(product._id);
        expect(deletedProduct).to.be.null;
    });

    // ... additional endpoint tests
});
